export function isValidGoogleNewsSearchArgs(args) {
    if (!args || typeof args !== "object") {
        return false;
    }
    const typedArgs = args;
    // 检查所有可选字段的类型
    if (typedArgs.q !== undefined && typeof typedArgs.q !== "string")
        return false;
    if (typedArgs.gl !== undefined && typeof typedArgs.gl !== "string")
        return false;
    if (typedArgs.hl !== undefined && typeof typedArgs.hl !== "string")
        return false;
    if (typedArgs.topic_token !== undefined && typeof typedArgs.topic_token !== "string")
        return false;
    if (typedArgs.publication_token !== undefined && typeof typedArgs.publication_token !== "string")
        return false;
    if (typedArgs.story_token !== undefined && typeof typedArgs.story_token !== "string")
        return false;
    if (typedArgs.section_token !== undefined && typeof typedArgs.section_token !== "string")
        return false;
    return true;
}
